chrome.contextMenus.create(
    {
        id:"Encode",
        contexts:["selection"],
        onclick:function(n,t){
            chrome.tabs.sendMessage(t.id,
                {
                    action:"updateSelection",
                    selection:n.selectionText,
                    text:Base64.encode(n.selectionText)
                }
            )
        },
        title:"Base64 Encode"
    }
),
chrome.contextMenus.create(
    {
        id:"Decode",
        contexts:["selection"],
        onclick:function(n,t){
            chrome.tabs.sendMessage(t.id,
                {
                    action:"updateSelection",
                    selection:n.selectionText,
                    text:Base64.decode(n.selectionText)
                }
            )
        },
        title:"Base64 Decode"
    }
);